mapper.select:
select course_id
from board
where board_id = #{boardId}

mapper.update:
update board
set board_title = #{requestDto.title}, board_content = #{requestDto.content}, board_thumbnail = #{requestDto.thumbnail}, course_id = #{requestDto.courseId}
where board_id = #{boardId}

update board
set hit = hit+1
where board_id = #{boardId}
