mapper.select:
select g.gugun_id, g.gugun_name
from gugun g
where g.sido_id = #{sidoCode}

select s.tspot_id, s.tspot_title, s.addr1, s.first_image, s.latitude, s.longitude, st.tspot_type_name from travel_spot s, travel_spot_type st where s.sido_id = #{sidoCode} and s.gugun_id = #{gugunCode} and s.tspot_type_id = st.tspot_type_id and st.tspot_type_name in (
#{type}
) and s.tspot_title like concat('%', #{word}, '%') order by CASE WHEN s.tspot_title = #{word} THEN 1 WHEN s.tspot_title LIKE concat(#{word},'%') THEN 2 WHEN s.tspot_title LIKE concat('%',#{word}) THEN 3 ELSE 4 END, s.tspot_title ASC
