mapper.select:
select user.user_id, user.user_login_id, user.user_name, user.user_email, user.user_pwd
from user
where user.user_id = #{userId}

select user.user_id, user.user_login_id, user.user_name, user.user_email, user.user_pwd
from user
where user.user_login_id = #{loginId}

select user.user_id, user.user_login_id, user.user_name, user.user_email, user.user_pwd
from user
where user.user_login_id = #{loginId} and user.user_pwd = #{password}

select user.refresh_token
from user
where user.user_login_id = #{loginId}

mapper.update:
update user
set user_pwd = #{requestDto.password}, user_name = #{requestDto.name}, user_email=#{requestDto.email}
where user_login_id = #{loginId}

update user
set refresh_token=#{refreshToken}
where user_login_id = #{loginId}

update user
set refresh_token = null
where user_login_id = #{loginId}
